import { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [data, setData] = useState([]);
  const [inputAddress, setInputAddress] = useState('');
  const apiKey = '8GJCS6VIZ9GEXSMKMMHVRTKVZG62AZWGK2';

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(
        `https://api.etherscan.io/api?module=account&action=txlist&address=${inputAddress}&startblock=0&endblock=99999999&page=1&offset=10&sort=desc&apikey=${apiKey}`
      );
      setData(result.data.result);
    };
    fetchData();
  }, [inputAddress]);

  return (
    <div>
      <h1>Ethereum Transactions</h1>
      <div>
        <label htmlFor="address">Ethereum Address:</label>
        <input
          type="text"
          id="address"
          value={inputAddress}
          onChange={(e) => setInputAddress(e.target.value)}
        />
      </div>
      <ul>
        {Array.isArray(data) && data.length > 0 ? (
          data.map((tx) => (
            <li key={tx.hash}>
              <div>
                <p>Tx Hash: {tx.hash}</p>
                <p>From: {tx.from}</p>
                <p>To: {tx.to}</p>
                <p>Value: {tx.value}</p>
                <p>Gas: {tx.gas}</p>
              </div>
            </li>
          ))
        ) : (
          <p>No transactions found.</p>
        )}
      </ul>
    </div>
  );
}

export default App;
