import { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [data, setData] = useState([]);
  const [inputAddress, setInputAddress] = useState('');
  const apiKey = '8GJCS6VIZ9GEXSMKMMHVRTKVZG62AZWGK2';

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(
        `https://api.etherscan.io/api?module=account&action=txlist&address=${inputAddress}&startblock=0&endblock=99999999&page=1&offset=10&sort=desc&apikey=${apiKey}`
      );
      setData(result.data.result);
    };
    fetchData();
  }, [inputAddress]);

  return (
    <div className="p-8">
      <h1 className="text-3xl font-bold mb-8">Ethereum Transactions</h1>
      <div className="mb-8">
        <label htmlFor="address" className="block mb-2">Ethereum Address:</label>
        <input
          type="text"
          id="address"
          value={inputAddress}
          onChange={(e) => setInputAddress(e.target.value)}
          className="border border-gray-400 py-2 px-4 rounded w-full"
        />
      </div>
      <ul>
        {Array.isArray(data) && data.length > 0 ? (
          data.map((tx) => (
            <li key={tx.hash} className="mb-8">
              <div className="bg-white border border-gray-400 p-4 rounded">
                <p className="font-bold mb-2">Tx Hash:</p>
                <p>{tx.hash}</p>
                <p className="font-bold mb-2">From:</p>
                <p>{tx.from}</p>
                <p className="font-bold mb-2">To:</p>
                <p>{tx.to}</p>
                <p className="font-bold mb-2">Value:</p>
                <p>{tx.value}</p>
              </div>
            </li>
          ))
        ) : (
          <p>No transactions found.</p>
        )}
      </ul>
    </div>
  );
}

export default App;
