import { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [data, setData] = useState([]);
  const [inputAddress, setInputAddress] = useState('');
  const apiKey = '8GJCS6VIZ9GEXSMKMMHVRTKVZG62AZWGK2';

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(
        `https://api.etherscan.io/api?module=account&action=txlist&address=${inputAddress}&startblock=0&endblock=99999999&page=1&offset=10&sort=desc&apikey=${apiKey}`
      );
      setData(result.data.result);
    };
    fetchData();
  }, [inputAddress]);

  return (
    <div className="container mx-auto">
      <h1 className="text-3xl font-bold mb-6">Ethereum Transactions</h1>
      <div className="mb-4">
        <label htmlFor="address" className="block text-gray-700 font-bold mb-2">
          Ethereum Address
        </label>
        <input
          type="text"
          id="address"
          value={inputAddress}
          onChange={(e) => setInputAddress(e.target.value)}
          placeholder="Enter an Ethereum address"
          className="border border-gray-400 p-2 w-full rounded-md"
        />
      </div>
      <ul>
        {data.map((tx) => (
          <li key={tx.hash} className="border-b border-gray-400 py-2">
            <div className="flex items-center">
              <p className="text-gray-700 font-bold mr-2">Tx Hash:</p>
              <p className="text-gray-600">{tx.hash}</p>
            </div>
            <div className="flex items-center">
              <p className="text-gray-700 font-bold mr-2">From:</p>
              <p className="text-gray-600">{tx.from}</p>
            </div>
            <div className="flex items-center">
              <p className="text-gray-700 font-bold mr-2">To:</p>
              <p className="text-gray-600">{tx.to}</p>
            </div>
            <div className="flex items-center">
              <p className="text-gray-700 font-bold mr-2">Value:</p>
              <p className="text-gray-600">{tx.value}</p>
            </div>
            <div className="flex items-center">
              <p className="text-gray-700 font-bold mr-2">Gas:</p>
              <p className="text-gray-600">{tx.gas}</p>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
