import React, { useState, useEffect } from 'react';
import axios from 'axios';


const App = () => {
  const [data, setData] = useState([]);
  const [inputAddress, setInputAddress] = useState('');

  const apiKey = '8GJCS6VIZ9GEXSMKMMHVRTKVZG62AZWGK2';
  const address = '0xbFc04670eD8d45660853Dd2286869319f4b4033e';

    // ...


  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(
        `https://api.etherscan.io/api?module=account&action=txlist&address=${address}&startblock=0&endblock=99999999&page=1&offset=10&sort=desc&apikey=${apiKey}`
      );
      setData(result.data.result);
    };
    fetchData();
  }, []);



  return (
    <div className="container mx-auto">
  <h1 className="text-3xl font-bold mb-6">Ethereum Transactions</h1>
  <div className="mb-4">
    <label htmlFor="address" className="block text-gray-700 font-bold mb-2">
      Ethereum Address
    </label>
    <input
      type="text"
      id="address"
      value={inputAddress}
      onChange={(e) => setInputAddress(e.target.value)}
      placeholder="Enter an Ethereum address"
      className="border border-gray-400 p-2 w-full"
    />
  </div>
  <ul>
    {data.map((tx) => (
      <li key={tx.hash}>
        <p className="text-lg mb-2">Hash: {tx.hash}</p>
        <p className="text-lg mb-2">Value: {tx.value}</p>
        <p className="text-lg mb-2">Gas Used: {tx.gasUsed}</p>
        <p className="text-lg mb-2">Sender address: {tx.from}</p>
        <p className="text-lg mb-2">Message: {tx.input}</p>
      </li>
    ))}
  </ul>
</div>

  );
};

export default App;
