import React, { useState, useEffect } from 'react';
import axios from 'axios';


const App = () => {
  const [data, setData] = useState([]);


  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(
        `https://api.etherscan.io/api?module=account&action=txlist&address=${address}&startblock=0&endblock=99999999&page=1&offset=10&sort=desc&apikey=${apiKey}`
      );
      setData(result.data.result);
    };
    fetchData();
  }, []);



  return (
    <div className="container mx-auto">
  <h1 className="text-3xl font-bold mb-6">Ethereum Transactions</h1>
  <p className="text-xl mb-2">Address: {address}</p>
  <ul>
    {data.map((tx) => (
      <li key={tx.hash}>
        <p className="text-lg mb-2">Hash: {tx.hash}</p>
        <p className="text-lg mb-2">Value: {tx.value}</p>
        <p className="text-lg mb-2">Gas Used: {tx.gasUsed}</p>
      </li>
    ))}
  </ul>
</div>

  );
};

export default App;
