import { useState, useEffect } from 'react';
import axios from 'axios';
import { Buffer } from 'buffer';
import Web3 from 'web3';

function App() {
  const [data, setData] = useState([]);
  const [inputAddress, setInputAddress] = useState('');
  const web3 = new Web3(window.ethereum);
  const apiKey = '8GJCS6VIZ9GEXSMKMMHVRTKVZG62AZWGK2';

  useEffect(() => {
    const isValidAddress = web3.utils.isAddress(inputAddress);
    if (!isValidAddress) {
      return;
    }
    const fetchData = async () => {
      const result = await axios(
        `https://api.etherscan.io/api?module=account&action=txlist&address=${encodeURIComponent(inputAddress)}&startblock=0&endblock=99999999&page=1&sort=desc&apikey=${apiKey}`
      );
      setData(result.data.result);
    };

    fetchData();
  }, [inputAddress]);

  const getTransactionLink = (txHash) => {
    return `https://etherscan.io/tx/${txHash}`;
  };

  const getHumanReadibleMessage = (hexString) => {
    try {
      return decodeURIComponent(escape(Buffer.from(hexString, 'hex').toString('utf8')));
    } catch (error) {
      console.log(error);
      return "Error: Unable to decode input data";
    }
  };

  return (
    <div className="p-8">
      <h1 className="text-3xl font-bold mb-8">Ethereum Transactions</h1>
      <div className="mb-8">
        <label htmlFor="address" className="block mb-2">
          Ethereum Address:
        </label>
        <input
          type="text"
          id="address"
          value={inputAddress}
          onChange={(e) => setInputAddress(e.target.value)}
          className="border border-gray-400 py-2 px-4 rounded w-full"
        />
      </div>
      <ul>
        {Array.isArray(data) && data.length > 0 ? (
          data
            .filter((tx) => tx.input !== '0x')
            .map((tx) => (
              <li key={tx.hash} className="mb-8">
                <div className="bg-gray-100 py-2 px-4 rounded-lg max-w-xl">
                  <div className="flex items-center justify-between">
                    <p className="font-bold">Tx Hash:</p>
                    <a href={getTransactionLink(tx.hash)} target="_blank" rel="noreferrer" className="text-blue-500 hover:underline">{tx.hash}</a>
                  </div>
                  <div className="flex items-start">
                    <div className="bg-blue-500 rounded-full w-8 h-8 flex items-center justify-center text-white font-bold mr-4 mt-1">
                      <p>{tx.from.substring(0,2)}</p>
                    </div>
                    <div>
                      <p className="text-sm text-gray-700">{getHumanReadibleMessage(tx.input.slice(2))}</p>
                    </div>
                  </div>
                </div>
              </li>
            ))
        ) : (
          <p>No transactions found.</p>
        )}
      </ul>
    </div>
  );
}

export default App;
